document.addEventListener("DOMContentLoaded",function(){const lazyElements=document
    .querySelectorAll(".lazy"),observer=new IntersectionObserver(entries=>{entries
        .forEach(entry=>{if(entry.isIntersecting){const lazyElement=entry
            .target;"IMG"===lazyElement.tagName?lazyElement.src=lazyElement
            .dataset.src:"VIDEO"===lazyElement.tagName&&(lazyElement.src=lazyElement.dataset.src),lazyElement
            .classList.remove("lazy"),observer.unobserve(lazyElement)}})});lazyElements
            .forEach(element=>observer.observe(element));const data={loadTime:500,pageSize:5000};document
            .getElementById("load-time").textContent=data.loadTime,document
            .getElementById("page-size").textContent=data.pageSize});
